    function distxy(x1, y1, x2, y2) {
        var dx = x2 - x1, dy = y2 - y1;
        return Math.sqrt(dx * dx + dy * dy);
    };
       
    function Point(x, y) {
        this.x = x;
        this.y = y;
    };
    
    
    Point.prototype.dist = function(p) { 
        return distxy(this.x, this.y, p.x, p.y);
    };
                
    
    function determinant(p1, p2, p3) { 
        return  (p2.x-p1.x)*(p3.y-p1.y)-(p2.y-p1.y)*(p3.x-p2.x);
    };
        
    function aGauche(p1, p2, p3) { 
        return determinant(p1, p2, p3) > 0; 
    };
//add public data to exported module
        
    exports.Point = Point;
    exports.aGauche = aGauche;